create table user
(
    id               int auto_increment
        primary key,
    facility_id      int                      not null,
    role_id          int                      not null,
    first_name       varchar(30) charset utf8 not null,
    second_name      varchar(30) charset utf8 null,
    surname          varchar(30) charset utf8 not null,
    e_mail           varchar(50) charset utf8 not null,
    hash_password    binary(60)               not null,
    avatar           varchar(10) charset utf8 not null,
    title            varchar(40)              null,
    birth_date       date                     null,
    change_pswd_link varchar(100)             null,
    link_time        datetime                 null,
    constraint e_mail
        unique (e_mail, change_pswd_link),
    constraint fk_user_facility
        foreign key (facility_id) references facility (id)
            on update cascade on delete cascade,
    constraint fk_user_role
        foreign key (role_id) references role (id)
            on update cascade on delete cascade
);

